export const CartHelper = class {
   static getCart() {
        if(!this.cartData){
            this.cartData = "test";
        } else {
            this.cartData = 'was already here';
        }
        return this.cartData;
    }

    static setCart(data) {
        this.cartData = Object.assign({}, this.cartData, data);
    }


    static removeFromCartById(id){
        this.cartData = this.cartData.filter( event => event.id !== event.id)


    }
}
