import React from "react";
import "./style.css";
import "../../pages/products";
import { Button, Icon } from 'semantic-ui-react'
import { CartHelper } from '../../utils/action';

class Payment extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      count: 1,
      ...props
    };
  }

  handleIncrement = () => {
    if (this.state.count > this.state.stock) {
      return alert("Out of Stock");
    }
    this.setState({ count: this.state.count + 1 });
  };

  handleDecrement = () => {
    if (this.state.count <= 1) {
      this.setState(prevState => ({ counter: prevState.count - 1 }));
    } else {
      this.setState({ count: this.state.count - 1 });
    }
  };

  onInputChange = value => {
    this.setState({ count: value });
  };

  addToCart = () => {
    console.log('state', this.state)
    let data = this.state;
    CartHelper.setCart(data);
  };

  render() {
    return (
      <div>
        <div className="payment-container">
          <button className="decrement" onClick={this.handleDecrement}>
            -
          </button>

          <input
            type="text"
            value={this.state.count}
            onChange={event => this.onInputChange(event.target.value)}
          />

          <button className="increment" onClick={this.handleIncrement}>
            +
          </button>
        </div>
        <Button  onClick={this.onClick}>
      <Button.Content className="ui button active" hidden>Added</Button.Content>
      <Button.Content  visible>
        Add to Cart
      </Button.Content>
    </Button>

    <button class="ui button active">Following</button>

    <button class="ui button">
  Follow
</button>

        <button className="add-to-cart" onClick={this.addToCart}>
          Add To Cart
        </button>
      </div>
    );
  }
}

export default Payment;
