import React from "react";
// import plants from "../plants.json";
import API from "../utils/API";
import PlantCard from "../components/PlantCard";
import Payment from "../components/Payment";
import SearchBar from "../components/SearchBar";

class Products extends React.Component {
  constructor(props) {
    super();
    this.state = {
      // plants: plants,
      plants: [],
      plantName: "",
      image: "",
      price: "",
      stock: ""
    };
  }

  componentDidMount() {
    this.loadPlants();
  }

  loadPlants = () => {
    API.getPlants().then(res =>
      this.setState({
        plants: res.data,
        plantName: "",
        image: " ",
        price: " ",
        stock: ""
      })
    );
  };

  handleSearch(event) {
    const value = event.target.value;
    console.log(
      value.toLowerCase(),
      plants[0].plantName.toLowerCase().search(value.toLowerCase()) === -1
    );
    const queriedObjs = plants.filter(
      event => event.plantName.toLowerCase().search(value.toLowerCase()) !== -1
    );
    this.setState({ plants: queriedObjs });
    console.log("state plants", this.state.plants);
  }

  render() {
    return (
      <div>
        <SearchBar handleSearch={this.handleSearch.bind(this)} />

        <div className="content-wrapper">
          {this.state.plants.length !== 0
            ? this.state.plants.map(
                ({ id, plantName, image, price, stock }) => (
                  <PlantCard
                    id={id}
                    key={id}
                    plantName={plantName}
                    image={image}
                    price={"$" + price}
                    stock={stock}
                  >
                    <Payment />
                  </PlantCard>
                )
              )
            : "NO RESULTS"}
        </div>
      </div>
    );
  }
}

export default Products;
