import React from "react";
import "./style.css";
import { Image, Modal } from "semantic-ui-react";

class Cart extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      price: 0
    };
  }

  componentDidMount = () => {
    let getCart = JSON.parse(localStorage.getItem("cart"));
    let plants = getCart[0];

    this.setState({price: plants.price})


    console.log("state" + this.state.price)

    console.log("these are your products" + getCart);
    console.log("this is your price:" + plants.price);
    console.log("this is your stock" + plants.stock);
  };

  render() {
    return (
      <div className="cart-modal">
        <Modal
          trigger={
            <button className="item">
              <i className="fas fa-shopping-cart" id="shopping-icon" />
            </button>
          }
        >
          <Modal.Header>
            <p> Your Cart </p>
          </Modal.Header>
          <Modal.Content image>
            <Modal.Description>
              <div className="cart-css">
                <p>Price: {this.state.price}</p>
              </div>
            </Modal.Description>

            <Image
              wrapped
              size="large"
              src="https://images.pexels.com/photos/1313807/pexels-photo-1313807.jpeg?auto=compress&cs=tinysrgb&dpr=2&h=650&w=1200"
            />
          </Modal.Content>
        </Modal>
      </div>
    );
  }
}

export default Cart;
