import React from "react";
import './style.css'
import '../../pages/products';
class Payment extends React.Component {
  constructor(props) {
    super(props);


    this.state = {
      count: 0
    };
  }

  handleIncrement = () => {
    this.setState({ count: this.state.count +1 });
  };

  handleDecrement = () => {
    if(this.state.count <= 0){
      this.setState(prevState => ({counter: prevState.count -1}))
    }
    else{
      this.setState({ count: this.state.count-1})
    }
  }

  onInput

  render() {
    return (
      <div>
      <div className="payment-container">



          <button className="decrement" onClick={this.handleDecrement}>-</button>


    <input type="text" value={this.state.count}
    onChange={event => this.onInputChange(event.target.value)}></input>


          <button className="increment" onClick={this.handleIncrement}>+</button>



      </div>

      <button className="add-to-cart">Add To Cart</button>
    </div>
    );
  } 
}

export default Payment;
