import React from "react";
import "./style.css";
import Payment from "../Payment";



const PlantCard = props => {
  Flip= () =>{
    let card = document.querySelector('.card');
    card.onClick = (e)=>{
      card.classList.toggle('flipped');
    }
  };
  return (
    <div className="plant-container">
    <div className="card">
      <div className="front">
      <div className="img-container">
      <button href="#" className="toggle-me" onClick={flip()}>Learn more</button>
        <img alt={props.plantName} src={props.image} />
      </div>
      <div className="content">
        <p> {props.plantName}</p>
        <p>${props.price}</p>
        {/* <p>{props.stock}</p>  */}
        <Payment
          key={props._id}
          id={props._id}
          image={props.image}
          plantName={props.plantName}
          price={props.price}
          stock={props.stock}
        />

      </div>
      </div>
      <div className="back">2</div>
    </div>
    </div>
  );
};

export default PlantCard;
