import React from "react";
import "./style.css";
import { Image, Modal } from "semantic-ui-react";
import { CartHelper } from '../../utils/action'

class Cart extends React.Component {
  constructor(props) {
    super();
    this.state = {
      cart: [],
      total: 0
    };
  }

