import React from "react";
import "./style.css";
import Payment from "../Payment";

class PlantCard extends React.Component {



toggleClass = () => {
  const old = document.getElementById('.card').class

  };

  render() {
    return (
      <div className="plant-container">
        <div className="card">
          <div className="front">
            <div className="img-container">
              <a href="#" className={this.state.active ? '.flipped': null} onClick={this.flip()}>
                Learn more
              </a>
              <img alt={this.props.plantName} src={this.props.image} />
            </div>
            <div className="content">
              <p> {this.props.plantName}</p>
              <p>${this.props.price}</p>
              {/* <p>{props.stock}</p>  */}
              <Payment
                key={this.props._id}
                id={this.props._id}
                image={this.props.image}
                plantName={this.props.plantName}
                price={this.props.price}
                stock={this.props.stock}
              />
            </div>
          </div>
          <div className="back">2</div>
        </div>
      </div>
    );
  }
}

export default PlantCard;
