import React from "react";
import "./style.css";
import { Button, Image, Modal } from "semantic-ui-react";
import { CartHelper } from "../../utils/action";

class Cart extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      products: CartHelper.getCart()
    };
  }
  render() {
    console.log(this.state.products);
    return (
      <div className="cart-modal">
        <Modal
          trigger={
            <a className="item">
              <i className="fas fa-shopping-cart" id="shopping-icon" />
            </a>
          }
        >
          <Modal.Header>Your Cart</Modal.Header>
          <Modal.Content image>
            <Image wrapped size="small" src="" />
            <Modal.Description><div> </div></Modal.Description>
          </Modal.Content>
        </Modal>
      </div>
    );
  }
}

export default Cart;
